mapper.select:
SELECT	USERID, PASSWORD, NAME, AGE, PHONE, GENRENUM, ADMIN
FROM		USERINFO
WHERE	USERID = #{userid} AND PASSWORD = #{password}

SELECT	USERID, PASSWORD, NAME, AGE, PHONE, GENRENUM, ADMIN
FROM		USERINFO
WHERE	USERID = #{userid}

mapper.update:
UPDATE	USERINFO
SET		PASSWORD = #{password},
		NAME = #{name},
		AGE = #{age},
		PHONE = #{phone}
WHERE	USERID = #{userid}
